function updatedWielded()
if snd.wielded.left.name == "" and snd.wielded.right.name == "" then wielding:echo("<center>empty hands</center>") else local string = "" if snd.wielded.left.name ~= "" then string = string .. "<center>" .. snd.weaponType(snd.wielded.left.name) .. "</center>" end if snd.wielded.right.name ~= "" then string = string .. "<center>" .. snd.weaponType(snd.wielded.right.name) .. "</center>" end wielding:echo(string) end
end registerAnonymousEventHandler("sunder_item_update","updatedWielded")

